async def load_recipes():
    """–ó–∞–≥—Ä—É–∑–∫–∞ —Ä–µ—Ü–µ–ø—Ç–æ–≤ –≤ –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö"""
    if not YOUR_MEALS_DATA or len(YOUR_MEALS_DATA) <= 1:
        print("‚ö†Ô∏è YOUR_MEALS_DATA –ø—É—Å—Ç–æ–π! –î–æ–±–∞–≤—å —Å–≤–æ–∏ —Ä–µ—Ü–µ–ø—Ç—ã.")
        return
    
    async with async_session() as session:
        # –ü—Ä–æ–≤–µ—Ä—è–µ–º –µ—Å—Ç—å –ª–∏ —É–∂–µ —Ä–µ—Ü–µ–ø—Ç—ã
        from sqlalchemy import select, func
        result = await session.execute(select(func.count(Recipe.id)))
        count = result.scalar()
        
        if count > 0:
            print(f"‚úÖ –í –±–∞–∑–µ —É–∂–µ {count} —Ä–µ—Ü–µ–ø—Ç–æ–≤, –ø—Ä–æ–ø—É—Å–∫–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É")
            return
        
        # –ó–∞–≥—Ä—É–∂–∞–µ–º –Ω–æ–≤—ã–µ
        loaded = 0
        for day_num, meals in YOUR_MEALS_DATA.items():
            for meal_type, data in meals.items():
                if meal_type == "total":
                    continue
                
                # –ò–∑–≤–ª–µ–∫–∞–µ–º —á–∏—Å–ª–æ –∫–∞–ª–æ—Ä–∏–π
                cal_text = data.get("calories", "0")
                cal_num = extract_calories(cal_text)
                
                # –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–µ–≥–∏
                tags = generate_tags(meal_type, data["title"])
                
                recipe = Recipe(
                    day_number=day_num,
                    meal_type=meal_type,
                    title=data["title"],
                    shopping=data["shopping"],
                    portion=data["portion"],
                    recipe=data["recipe"],
                    calories_text=data["calories"],
                    calories_value=cal_num,
                    is_premium=(day_num > 7),  # –ü–µ—Ä–≤—ã–µ 7 –¥–Ω–µ–π –±–µ—Å–ø–ª–∞—Ç–Ω–æ
                    tags=tags
                )
                session.add(recipe)
                loaded += 1
        
        await session.commit()
        print(f"‚úÖ –ó–∞–≥—Ä—É–∂–µ–Ω–æ {loaded} —Ä–µ—Ü–µ–ø—Ç–æ–≤!")

def extract_calories(text: str) -> int:
    """–ò–∑–≤–ª–µ—á—å —á–∏—Å–ª–æ –∫–∞–ª–æ—Ä–∏–π –∏–∑ —Ç–µ–∫—Å—Ç–∞"""
    numbers = re.findall(r'(\d+)\s*–∫–∫–∞–ª', text.lower())
    return int(numbers[0]) if numbers else 0

def generate_tags(meal_type: str, title: str) -> list:
    """–°–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å —Ç–µ–≥–∏ –¥–ª—è –ø–æ–∏—Å–∫–∞"""
    tags = [meal_type]
    t = title.lower()
    
    # –¢–∏–ø—ã –º—è—Å–∞
    if "–∫—É—Ä" in t:
        tags.append("–∫—É—Ä–∏—Ü–∞")
    if "—Å–≤–∏–Ω" in t:
        tags.append("—Å–≤–∏–Ω–∏–Ω–∞")
    if "–≥–æ–≤—è" in t:
        tags.append("–≥–æ–≤—è–¥–∏–Ω–∞")
    if "–∏–Ω–¥–µ–π" in t:
        tags.append("–∏–Ω–¥–µ–π–∫–∞")
    if "—Ä—ã–±" in t or "–ª–æ—Å–æ—Å" in t or "—Ç—Ä–µ—Å–∫" in t:
        tags.append("—Ä—ã–±–∞")
    
    # –°–ø–æ—Å–æ–± –ø—Ä–∏–≥–æ—Ç–æ–≤–ª–µ–Ω–∏—è
    if "–∞—ç—Ä–æ–≥—Ä–∏–ª" in t:
        tags.append("–∞—ç—Ä–æ–≥—Ä–∏–ª—å")
    if "–¥—É—Ö–æ–≤" in t:
        tags.append("–¥—É—Ö–æ–≤–∫–∞")
    if "—Å–∫–æ–≤–æ—Ä–æ–¥" in t:
        tags.append("—Å–∫–æ–≤–æ—Ä–æ–¥–∞")
    
    # –¢–∏–ø—ã –±–ª—é–¥
    if "–æ–≤—Å—è–Ω" in t or "–∫–∞—à" in t:
        tags.append("–∫–∞—à–∞")
    if "–æ–º–ª–µ—Ç" in t or "—è–∏—á–Ω" in t:
        tags.append("—è–π—Ü–∞")
    if "—Ç–≤–æ—Ä–æ–≥" in t or "—Å—ã—Ä–Ω–∏–∫" in t:
        tags.append("—Ç–≤–æ—Ä–æ–≥")
    if "—Å—É–ø" in t:
        tags.append("—Å—É–ø")
    if "—Å–∞–ª–∞—Ç" in t:
        tags.append("—Å–∞–ª–∞—Ç")
    
    # –û—Å–æ–±–µ–Ω–Ω–æ—Å—Ç–∏
    if any(x in t for x in ["–±—ã—Å—Ç—Ä–æ", "10 –º–∏–Ω", "15 –º–∏–Ω", "5 –º–∏–Ω"]):
        tags.append("–±—ã—Å—Ç—Ä–æ")
    if any(x in t for x in ["–∑–∞–ø–µ–∫–∞–Ω–∫", "–∑–∞–ø–µ—á–µ–Ω"]):
        tags.append("–∑–∞–ø–µ–∫–∞–Ω–∏–µ")
    
    return tags